import { Component ,OnInit} from '@angular/core';
import {HttpClient, HttpClientModule} from '@angular/common/http';
@Component({
  selector: 'app-draft-table',
  templateUrl: './draft-table.component.html',
  styleUrls: ['./draft-table.component.css']
})
export class DraftTableComponent implements OnInit {
    posts:any[] =[];

    constructor(private http : HttpClient){}

    ngOnInit(){
      this.http.get<any[]>('http://localhost:3000/posts').subscribe(posts =>{
        this.posts =posts;
        console.log(posts);
      })
    }


}
<table class="table table-bordered">
    <thead>
        <tr>
            <td style=" background-color: rgba(223, 175, 46, 0.705);">S.No</td>
            <td style=" background-color: rgba(223, 175, 46, 0.705);">Title Of Notice</td>
            <td style=" background-color: rgba(223, 175, 46, 0.705);"> Jurisdistion</td>
            <td style=" background-color: rgba(223, 175, 46, 0.705);">Corporate Function</td>
            <td style=" background-color: rgba(223, 175, 46, 0.705);">Draft pending since</td>
            <td style=" background-color: rgba(223, 175, 46, 0.705);">Status</td>                                                     
            <td style=" background-color: rgba(223, 175, 46, 0.705);">Action</td>


        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let post of posts">
            <td>{{post.id}}</td>
            <td>{{post.TitleOfNotice}}</td>
            <td>{{post.Jurisdiction}}</td>
            <td>{{post.CorporateFunction}}</td>
        </tr>
    </tbody>
</table>
