import { Component } from '@angular/core';

@Component({
  selector: 'app-notice-type',
  templateUrl: './notice-type.component.html',
  styleUrls: ['./notice-type.component.css']
})
export class NoticeTypeComponent {
  selectedOption : string ='submitted'   ;
  filteredValue: string ='';


  onRadioChange(option :string){
    this.selectedOption=option;


  }
  applyFilter(value : string){
    this.filteredValue =value
  }
}
<!-- filter.component.html -->

<div id="fun"> 
    <b>
      Select View :
  </b>&nbsp;&nbsp;
  <!-- <label ><input type="radio" name="submitedNotice" value="submitedNotice">Submitted Notice</label>
  <label ><input type="radio" name="submitedNotice" value="submitedNotice">Drafted Notice</label> -->
  <label> <input type="radio" name="noticeType" value="submitted" [(ngModel)]="selectedOption" (change)="onRadioChange('submitted')" >Submitted Notices</label>&nbsp;&nbsp;
  <label>  <input type="radio" name="noticeType" value="drafted" [(ngModel)]="selectedOption" (change)="onRadioChange('drafted')"> Draft Notices</label>
</div>


  <!-- Conditionally display submitted or drafted content based on the selected radio button -->
  <div *ngIf="selectedOption === 'submitted'">
    <!-- Your submitted screen content goes here -->
    <app-filter></app-filter>
    <!-- <h1>submited screen</h1> -->
  </div>

  <div *ngIf="selectedOption === 'drafted'">
    <!-- Your drafted screen content goes here -->
    <!-- <app-table-data></app-table-data> -->
    <app-drafted-notice (filterApplied)="applyFilter($event)"></app-drafted-notice>
    <!-- <p>drafted page </p> -->
  </div>
