import { Component } from '@angular/core';

@Component({
  selector: 'app-drafted-notice',
  templateUrl: './drafted-notice.component.html',
  styleUrls: ['./drafted-notice.component.css']
})
export class DraftedNoticeComponent {

@Output() filterApplied = new  EventEmitter<string>();

applyFilter (value :string){
  this.filterApplied.emit(value);
}
}
