import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-drafted-notice',
  templateUrl: './drafted-notice.component.html',
  styleUrls: ['./drafted-notice.component.css']
})
export class DraftedNoticeComponent {

  posts:any[] =[];

  constructor(private http : HttpClient){}

  ngOnInit(){
    this.http.get<any[]>('http://localhost:3000/posts').subscribe(posts =>{
      this.posts =posts;
      console.log(posts);
    })
  }

}
